import React, { Component } from "react";
import DataTable from "./DataTable";
import DataSearch from "./DataSearch";
import axios from "axios";

export class Data extends Component {
  state = {
    data: [],
    query: ""
  };

  componentDidMount = () => {
    this.getData();
  };

  getData = () => {
    axios
      .get("/search")
      .then(response => {
        this.setState({
          data: response
        });
      })
      .catch(err => {
        console.log(err);
      });
  };

  setQuery = query => {
    this.setState({
      query: event.target.value
    });
  };

  render() {
    return (
      <div>
        <DataSearch setQuery={this.setQuery}/>
        <DataTable data={this.state.data.data} />
      </div>
    );
  }
}

export default Data;
