import React, { Component } from "react";

export class DataTable extends Component {
  render() {
    let { data, query } = this.props;

    if (data) {
        if(query === "") {
            return
        } else 
      if (query !== "") {
        // data = data.filter(person => {
        //   return Object.values(person).includes(query);
        // });
        data.filter(person => Object.values(person).includes(query));
      }

      if (this.props.query === "") {
        users = this.props.users;
      } else {
        users = this.props.users.filter(value => {
          return value.username
            .toLowerCase()
            .includes(this.props.query.toLowerCase());
        });
      }

      return (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Eye color</th>
              <th>Gender</th>
              <th>Company</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Address</th>
            </tr>
          </thead>
          <tbody>
            {data.map(person => {
              return (
                <tr key={person._id}>
                  <td>{person.name}</td>
                  <td>{person.age}</td>
                  <td>{person.eyeColor}</td>
                  <td>{person.gender}</td>
                  <td>{person.company}</td>
                  <td>{person.email}</td>
                  <td>{person.phone}</td>
                  <td>{person.address}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      );
    } else {
      console.log("render empty div");
      return <div></div>;
    }
  }
}

export default DataTable;
